  {id: 22, step_type: "embed", credits_url: "", mask_sql: "select * from gadm28_adm0 where iso != 'ETH'", map_url: "/map?layers=%5B%7B%22id%22%3A66%2C%22opacity%22%3A1%2C%22order%22%3A2%7D%5D&zoom=6&center=%7B%22lat%22%3A9.09124858577939%2C%22lng%22%3A38.03466796875%7D", embedded_map_url: "/embed/map?layers=%5B%7B%22id%22%3A38%2C%22opacity%22%3A1%2C%22order%22%3A0%7D%5D&zoom=6&center=%7B%22lat%22%3A9.09124858577939%2C%22lng%22%3A38.03466796875%7D", background_color: "#000000", chapter_number: nil, position: 4, journey_id: 5, title: "Livelihoods", subtitle: "Pastoral", description: nil, content: "<div>Lowlands are home to 29 ethnic groups, of which &gt;90% are pastoralists</div>", credits: nil, source: nil},
