  {id: 61, layer_group_id: 27, name: "Projected change (2040-2060, RCP8.5)", slug: "temp-proj-3", layer_type: "layer", zindex: nil, active: false, order: nil, color: "", info: "{\"description\":\"Change in annual mean daily mean surface temperature for 2040-2060, in degrees Celsius, compared to the 1980-2000 period. This layer uses downscaled data from the 5th Coupled Model Intercomparison Project (CMIP5) made available in the the NASA Earth Exchange Global Daily Downscaled Projections (NEX-GDDP) dataset. The data for this layer is from the RCP8.5 scenario, a scenario that results in CO2 concentrations of more than 1370 ppm by 2100.\", \"source\":\"<p>Moss, Richard H., Jae A. Edmonds, Kathy A. Hibbard, Martin R. Manning, Steven K. Rose, Detlef P. van Vuuren, Timothy R. Carter, et al. 'The next Generation of Scenarios for Climate Change Research and Assessment.' Nature 463, no. 7282 (February 11, 2010): 747–56.</p><p>Taylor, Karl E., Ronald J. Stouffer, and Gerald A. Meehl. 'An Overview of CMIP5 and the Experiment Design.' Bulletin of the American Meteorological Society 93, no. 4 (2012): 485–98.</p>\", \"link\":\"http://dx.doi.org/10.7292/W0MW2F2G\" }", layer_provider: "raster", css: "#meandailytemp_rcp85_1986_2005_vs_2040_2059_annual {raster-opacity:0.8; raster-scaling:near; raster-colorizer-default-mode:discrete; raster-colorizer-default-color: transparent; raster-colorizer-epsilon:0.41; raster-colorizer-stops: stop(0, #fff) stop(1, #ff7575) stop(2, #fd5454) stop(3, #e00c0c) stop(4, #b00303) stop(5, #970202) stop(6, #862D63) stop(7, #641144) stop(10, #430029)}", interactivity: "", opacity: 1.0, query: "select * from meandailytemp_rcp85_1986_2005_vs_2040_2059_annual", created_at: "2015-10-22 08:41:51", updated_at: "2015-10-22 09:24:15", locate_layer: false, icon_class: "", published: true, legend: "{ \"min\": \"0 °C\", \"mid\":\"4 °C\", \"max\": \"10 °C\",\"units\":\"increase from 1980-2000 mean\", \"type\": \"choropleth\", \"bucket\": [ \"#fff\", \"#ff7575\", \"#fd5454\", \"#b00303\", \"#970202\", \"#862D63\",\"#641144\", \"#430029\"] }"},
  {id: 57, layer_group_id: 26, name: "Projected change (2040-2060, RCP8.5)", slug: "ppt-change-85-1", layer_type: "layer", zindex: nil, active: false, order: nil, color: "", info: "{\"description\":\"Percent change in mean total annual precipitation for 2040-2060, compared to the 1980-2000 period. This layer uses downscaled data from the 5th Coupled Model Intercomparison Project (CMIP5) made available in the the NASA Earth Exchange Global Daily Downscaled Projections (NEX-GDDP) dataset. The data for this layer is from the RCP8.5 scenario, a scenario that results in CO2 concentrations of more than 1370 ppm by 2100.\", \"source\":\"<p>\"Moss, Richard H., Jae A. Edmonds, Kathy A. Hibbard, Martin R. Manning, Steven K. Rose, Detlef P. van Vuuren, Timothy R. Carter, et al. “The next Generation of Scenarios for Climate Change Research and Assessment.” Nature 463, no. 7282 (February 11, 2010): 747–56.</p><p>Taylor, Karl E., Ronald J. Stouffer, and Gerald A. Meehl. 'An Overview of CMIP5 and the Experiment Design' Bulletin of the American Meteorological Society 93, no. 4 (2012): 485–98. '</p>\", \"link\":\"http://dx.doi.org/10.7292/W0MW2F2G\" }", layer_provider: "raster", css: "#pr_rcp85_pctdiff_1986_2005_vs_2040_2059_annual { raster-opacity:1; raster-scaling:near; raster-colorizer-default-mode:linear; raster-colorizer-default-color: transparent; raster-colorizer-epsilon:0.41; raster-colorizer-stops: stop(-35, rgba(111,103,78,1)) stop(-25, rgba(111,103,78,0.8)) stop(-15, rgba(111,103,78,0.6)) stop(-5, rgba(111,103,78,0.4)) stop(0, #fff) stop(5, #d0edf9) stop(15, #8ed2f0) stop(25, #88C1DF) stop(35, #1B6CAA) stop(50, #9341f9) stop(100, #9341f9); }", interactivity: "", opacity: nil, query: "select * from pr_rcp85_pctdiff_1986_2005_vs_2040_2059_annual", created_at: "2015-10-21 17:22:44", updated_at: "2015-10-22 10:01:41", locate_layer: false, icon_class: "", published: true, legend: "{ \"min\": \"-35%\", \"mid\":\"0%\", \"max\": \"50%\",\"units\":\"Percent of 1980-2000 mean\",  \