  {id: 63, layer_group_id: 24, name: "Trend in total annual precipitation", slug: "precp-trends", layer_type: "layer", zindex: nil, active: false, order: nil, color: "", info: "{\"description\":\"Trend in mean annual precipitation over the 1988-2015 period. Calculated as percentage change relative to the mean over that period from the Climate Hazards Group InfraRed Precipitation with Station (CHIRPS) version 2.0 dataset.\", \"source\":\"<p>Funk, C.C., Peterson, P.J., Landsfeld, M.F., Pedreros, D.H., Verdin, J.P., Rowland, J.D., Romero, B.E., Husak, G.J., Michaelsen, J.C., and Verdin, A.P., 2014, A quasi-global precipitation time series for drought monitoring: U.S. Geological Survey Data Series 832, 4 p., http://dx.doi.org/10.3133/ds832..</p>\", \"link\":\"http://chg.geog.ucsb.edu/data/chirps/\" }", layer_provider: "raster", css: "#chirps_annualppt_trend_pct_per_decade{\r\n        raster-opacity:1;\r\n        raster-opacity:1; \r\n  \traster-colorizer-default-mode: discrete; \r\n  \traster-colorizer-default-color: transparent; \r\n  \traster-colorizer-epsilon: 0.01; \r\n  \traster-colorizer-stops: \r\n    \tstop(-1,transparent) \r\n    \tstop(-0.13,#6F674E) \r\n    \tstop(-0.1,#C2BDAC) \r\n    \tstop(-0.03,#E1DED6) \r\n    \tstop(-0.001,#F2F9FC) \r\n    \tstop(-0.0001,#FFFFFF) \r\n    \tstop(0,transparent) \r\n    \tstop(0.0001,#F2F9FC) \r\n    \tstop(0.01, #CAE6F3) \r\n    \tstop(0.03, #88C1DF) \r\n    \tstop(0.05, #1B6CAA) \r\n        stop(0.13, #1B6CAA) \r\n    \tstop(1, transparent)\r\n\r\n\r\n}", interactivity: "", opacity: 1.0, query: "select * from chirps_annualppt_trend_pct_per_decade", created_at: "2015-10-22 09:51:40", updated_at: "2015-10-22 09:55:59", locate_layer: false, icon_class: "", published: true, legend: " {\"min\": \"-30%/decade\",\"mid\":\"0\", \"max\": \"30%/decade\", \"type\": \"choropleth\", \"bucket\": [ \"#6F674E\", \"#C2BDAC\", \"#E1DED6\", \"#FFFFFF\", \"#CAE6F3\", \"#88C1DF\", \"#1B6CAA\" ] }"},
  {id: 67, layer_group_id: 19, name: "Flood frequency", slug: "flood-tif", layer_type: "layer", zindex: nil, active: false, order: 1, color: "", info: "{\"description\":\"Number of large flood events from 1985 - 2015.\", \"source\":\"Data from the Dartmouth Flood Observatory at the University of Colorado, download date: September 16, 2015. Citation: G.R.Brakenridge, 'Global Active Archive of Large Flood Events', Dartmouth Flood Observatory, University of Colorado. \", \"link\":\"http://floodobservatory.colorado.edu/Archives/index.html\" }", layer_provider: "raster", css: "#grp_flood_frequency_floodarchive_20151021 {raster-opacity:0.8;\r\n  raster-scaling:near;\r\n  raster-colorizer-default-mode:linear;\r\n  raster-colorizer-default-color:  transparent;\r\n  raster-colorizer-epsilon:0.41;\r\n  raster-colorizer-stops:\r\n    stop(0, transparent)\r\n    stop(1, #FFFFB2)\r\n    stop(9, #FECC5C)\r\n\tstop(18, #FD8D3C)\r\n    stop(27, #F03B20)\r\n    stop(43, #BD0026)\r\n    stop(100, #BD0026)}", interactivity: "", opacity: 1.0, query: "select* from grp_flood_frequency_floodarchive_20151021", created_at: "2015-10-22 11:52:04", updated_at: "2015-10-22 11:55:17", locate_layer: false, icon_class: "", published: true, legend: "{ \"min\": \"1 floods\",\"mid\":\"15 floods\", \"max\": \"40 floods\", \"type\": \"choropleth\", \"bucket\": [ \"#FFFFB2\", \"#FECC5C\", \"#FD8D3C\", \"#F03B20\", \"#BD0026\" ] }"},
  {id: 4, layer_group_id: 17, name: "Top crops by area", slug: "Top_crops_by_area", layer_type: "layer", zindex: nil, active: false, order: 1, color: "", info: "{\"description\":\"Data is from the Spatial Production Allocation Model (SPAM) dataset. SPAM disaggregates national and sub national statistics to a 10x10km grid using a modeling approach that takes into account land cover and crop suitability. The SPAM dataset uses statistics from the three years surrounding 2005.\", \"source\":\"You, L., U. Wood-Sichra, S. Fritz, Z. Guo, L. See, and J. Koo. 2014. Spatial Production Allocation Model (SPAM) 2005 v2.0.\", \"link\":\"http://www.mapspam.info\" }", layer_provider: "cartodb", css: "#production_crops_e_all_data_grp {\r\n   polygon-opacity: 0.7;\r\n   line-color: #FFF;\r\n   line-width: 0.5;\r\n   line-opacity: 1;\r\n}\r\n\r\n#production_crops_e_all_data_grp[item=\"Beans, dry\"] {\r\n   polygon-fill: #A6CEE3;\r\n}\r\n#production_crops_e_all_data_grp[item=\"Cereals (Rice Milled Eqv)\"] {\r\n   polygon-fill: #1F78B4;\r\n}\r\n#production_crops_e_all_data_grp[item=\"Cereals,Total\"] {\r\n   polygon-fill: #B2DF8A;\r\n}\r\n#production_crops_e_all_data_grp[item=\"Fruit excl Melons,Total\"] {\r\n   polygon-fill: #33A02C;\r\n}", interactivity: "{\"Item\":\"item\", \"Year\":\"y2013\", \"Unit\":\"unit\"}", opacity: 1.0, query: " with t1 as (select element,unit, item, f.iso3, y2013,  row_number() over ( partition by f.iso3 order by y2013 desc) as rn from production_crops_e_all_data_grp f where y2013 is not null and element= 'Area harvested')\r\nSELECT element, t1.iso3, item, y2013, unit, the_geom, the_geom_webmercator FROM t1 INNER JOIN grpcountries_250k_polygon t2 ON t1.iso3 = t2.iso3 where rn = 1", created_at: "2015-08-03 05:21:40", updated_at: "2015-10-22 14:05:03", locate_layer: false, icon_class: "", published: true, legend: "{\"type\": \"custom\", \"data\": [ { \"name\": \"Beans, dry\", \"value\": \"#A6CEE3\" }, { \"name\": \"Rice Milled Eqv\", \"value\": \"#1F78B4\" }, { \"name\": \"Cereals,Total\", \"value\": \"#B2DF8A\" }, { \"name\": \"Fruit excl Melons,Total\", \"value\": \"#33A02C\" } ] }"},
  {id: 55, layer_group_id: 26, name: "Projected change (2040-2060, RCP4.5)", slug: "ppt-change-45-1", layer_type: "layer", zindex: nil, active: false, order: nil, color: "", info: "{\"description\":\"Percent change in mean total annual precipitation for 2040-2060, compared to the 1980-2000 period. This layer uses downscaled data from the 5th Coupled Model Intercomparison Project (CMIP5) made available in the the NASA Earth Exchange Global Daily Downscaled Projections (NEX-GDDP) dataset. The data for this layer is from the RCP4.5 scenario, a scenario that results in CO2 concentrations stabilizing at about 650 ppm by 2100.\", \"source\":\"<p>\"Moss, Richard H., Jae A. Edmonds, Kathy A. Hibbard, Martin R. Manning, Steven K. Rose, Detlef P. van Vuuren, Timothy R. Carter, et al. “The next Generation of Scenarios for Climate Change Research and Assessment.” Nature 463, no. 7282 (February 11, 2010): 747–56.</p><p>Taylor, Karl E., Ronald J. Stouffer, and Gerald A. Meehl. 'An Overview of CMIP5 and the Experiment Design' Bulletin of the American Meteorological Society 93, no. 4 (2012): 485–98.\r\n'</p>\", \"link\":\"http://dx.doi.org/10.7292/W0MW2F2G\" }", layer_provider: "raster", css: "#pr_rcp45_pctdiff_1986_2005_vs_2040_2059_annual {\r\nraster-opacity:1;\r\n  raster-scaling:near;\r\n  raster-colorizer-default-mode:linear;\r\n  raster-colorizer-default-color:  transparent;\r\n  raster-colorizer-epsilon:0.41;\r\n  raster-colorizer-stops:\r\n   stop(-35, rgba(111,103,78,1))\r\n    stop(-25, rgba(111,103,78,0.8))\r\n    stop(-15, rgba(111,103,78,0.6))\r\n    stop(-5, rgba(111,103,78,0.4))\r\n    stop(0, #fff)\r\n    stop(5, #d0edf9)\r\n    stop(15, #8ed2f0)\r\n    stop(25, #88C1DF)\r\n    stop(35, #1B6CAA)\r\n    stop(50, #9341f9) \r\n    stop(100, #9341f9);\r\n}", interactivity: "", opacity: 1.0, query: "select * from  pr_rcp45_pctdiff_1986_2005_vs_2040_2059_annual", created_at: "2015-10-21 16:31:31", updated_at: "2015-10-22 10:00:10", locate_layer: false, icon_class: "", published: true, legend: "{ \"min\": \"-35%\", \"mid\":\"0%\", \"max\": \"50%\",\"units\":\"Percent of 1980-2000 mean\", \"type\": \"choropleth\", \"bucket\": [ \"#6F674E\", \"#C2BDAC\", \"#E1DED6\", \"#FFFFFF\", \"#CAE6F3\", \"#88C1DF\", \"#1B6CAA\",\"#9341f9\" ] }"},
  {id: 58, layer_group_id: 26, name: "Projected change (2080-2100, RCP8.5)", slug: "ppt-change-85-2", layer_type: "layer", zindex: nil, active: false, order: nil, color: "", info: "{\"description\":\"Percent change in mean total annual precipitation for 2800-2100, compared to the 1980-2000 period. This layer uses downscaled data from the 5th Coupled Model Intercomparison Project (CMIP5) made available in the the NASA Earth Exchange Global Daily Downscaled Projections (NEX-GDDP) dataset. The data for this layer is from the RCP8.5 scenario, a scenario that results in CO2 concentrations of more than 1370 ppm by 2100.\", \"source\":\"<p>\"Moss, Richard H., Jae A. Edmonds, Kathy A. Hibbard, Martin R. Manning, Steven K. Rose, Detlef P. van Vuuren, Timothy R. Carter, et al. “The next Generation of Scenarios for Climate Change Research and Assessment.” Nature 463, no. 7282 (February 11, 2010): 747–56.</p><p>Taylor, Karl E., Ronald J. Stouffer, and Gerald A. Meehl. 'An Overview of CMIP5 and the Experiment Design' Bulletin of the American Meteorological Society 93, no. 4 (2012): 485–98. '</p>\", \"link\":\"http://dx.doi.org/10.7292/W0MW2F2G\" }", layer_provider: "raster", css: "#pr_rcp85_pctdiff_1986_2005_vs_2080_2099_annual{\r\nraster-opacity:1; raster-scaling:near; raster-colorizer-default-mode:linear; raster-colorizer-default-color: transparent; raster-colorizer-epsilon:0.41; raster-colorizer-stops: stop(-35, rgba(111,103,78,1)) stop(-25, rgba(111,103,78,0.8)) stop(-15, rgba(111,103,78,0.6)) stop(-5, rgba(111,103,78,0.4)) stop(0, #fff) stop(5, #d0edf9) stop(15, #8ed2f0) stop(25, #88C1DF) stop(35, #1B6CAA) stop(50, #9341f9) stop(100, #9341f9); \r\n}", interactivity: "", opacity: 1.0, query: "select * from pr_rcp85_pctdiff_1986_2005_vs_2080_2099_annual", created_at: "2015-10-21 17:29:29", updated_at: "2015-10-22 10:02:37", locate_layer: false, icon_class: "", published: true, legend: "{ \"min\": \"-35%\", \"mid\":\"0%\", \"max\": \"50%\",\"units\":\"Percent of 1980-2000 mean\",  \"type\": \"choropleth\", \"bucket\": [ \"#6F674E\", \"#C2BDAC\", \"#E1DED6\", \"#FFFFFF\", \"#CAE6F3\", \"#88C1DF\", \"#1B6CAA\",\"#9341f9\" ] }"},
  {id: 65, layer_group_id: 25, name: "Trend of mean annual mean daily temperature", slug: "temp-trend-anual", layer_type: "layer", zindex: nil, active: false, order: nil, color: "", info: "{\"description\":\"Trend of annual mean daily mean temperature in degrees Celsius per decade during the 1985-2015 period. Analysis based on monthly data from 1985-2015 from the Climatic Research Unit (CRU) TS Version 3.23 dataset.\", \"source\":\"<p>Harris, I., P.D. Jones, T.J. Osborn, and D.H. Lister. “The CRU TS3.10 Dataset: Updated High-Resolution Grids of Monthly Climatic Observations.” International Journal of Climatology 34, no. 3 (March 15, 2014): 623–42.</p>\", \"link\":\"http://www.cru.uea.ac.uk/cru/data/hrg\" }", layer_provider: "raster", css: "#cru_ts3_23_19850101_20141201_tmp_trend_deg_per_decade {raster-opacity:1;\r\n  raster-scaling:near;\r\n  raster-colorizer-default-mode:linear;\r\n  raster-colorizer-default-color:  transparent;\r\n  raster-colorizer-epsilon:0.41;\r\n  raster-colorizer-stops:\r\n    stop(-1, transparent)\r\n    stop(-0.6, #063D68)\r\n    stop(-0.4, #0B558F)\r\n    stop(-0.3, #2C72A7)\r\n    stop(-0.2, #4E8DBD)\r\n    stop(-0.1, #87B6DA)\r\n    stop(0, #FFF)\r\n    stop(0.2, #FF8080)\r\n    stop(0.3, #DD4242)\r\n    stop(0.4, #BC1D1D)\r\n    stop(0.5, #910101)\r\n    stop(0.6, #5B0000)\r\n    stop(0.9, #5B0000)\r\n    stop(1, transparent)}", interactivity: "", opacity: 1.0, query: "select st_clip(the_raster_webmercator, the_geom_webmercator) as the_raster_webmercator from cru_ts3_23_19850101_20141201_tmp_trend_deg_per_decade t, grpcountries_250k_polygon geom where st_intersects( t.the_raster_webmercator, geom.the_geom_webmercator)", created_at: "2015-10-22 10:55:27", updated_at: "2015-10-22 11:12:39", locate_layer: false, icon_class: "", published: true, legend: "{\"min\": \"-0.6°C/Decada\",\"mid\":\"0\", \"max\": \"0.6°C/Decada\", \"type\": \"choropleth\", \"bucket\": [ \"#063D68\", \"#0B558F\", \"#2C72A7\", \"#4E8DBD\", \"#87B6DA\", \"#FFF\", \"#F