  {id: 64, layer_group_id: 25, name: "Mean annual mean daily temperature", slug: "daily-mean-temp", layer_type: "layer", zindex: nil, active: false, order: nil, color: "", info: "{\"description\":\"Annual mean daily mean temperature in degrees Celsius during the 1985-2015 period. Analysis based on monthly data from 1985-2015 from the Climatic Research Unit (CRU) TS Version 3.23 dataset.\", \"source\":\"<p>Harris, I., P.D. Jones, T.J. Osborn, and D.H. Lister. “The CRU TS3.10 Dataset: Updated High-Resolution Grids of Monthly Climatic Observations.” International Journal of Climatology 34, no. 3 (March 15, 2014): 623–42.</p>\", \"link\":\"http://www.cru.uea.ac.uk/cru/data/hrg\" }", layer_provider: "raster", css: "#cru_ts3_23_19850101_20141201_tmp_annualmean_deg {\r\nraster-opacity:1;\r\n  raster-scaling:near;\r\n  raster-colorizer-default-mode:linear;\r\n  raster-colorizer-default-color:  transparent;\r\n  raster-colorizer-epsilon:0.41;\r\n  raster-colorizer-stops:\r\n    stop(-100, transparent)\r\n    stop(-20, #01465A)\r\n    stop(-15, #01465A)\r\n    stop(-10, #0193BC)\r\n    stop(-5, #05B8EB)\r\n    stop(0, #35BBE1)\r\n    stop(5, #A6D281)\r\n    stop(10, #6CA140)\r\n    stop(15, #487B1E)\r\n    stop(20, #FF8148)\r\n    stop(25, #FF2817)\r\n    stop(30, #940B00)\r\n    stop(35, #940B00)\r\n    stop(100, transparent)\r\n}", interactivity: "", opacity: 1.0, query: "select st_clip(the_raster_webmercator, the_geom_webmercator) as the_raster_webmercator from cru_ts3_23_19850101_20141201_tmp_annualmean_deg t, grpcountries_250k_polygon geom where st_intersects( t.the_raster_webmercator, geom.the_geom_webmercator) ", created_at: "2015-10-22 10:31:26", updated_at: "2015-10-22 11:06:43", locate_layer: false, icon_class: "", published: true, legend: "{\"min\": \"-20°C\",\"mid\":\"5\", \"max\": \"30°C\", \"type\": \"choropleth\", \"bucket\": [ \"#01465A\", \"#0193BC\", \"#05B8EB\", \"#35BBE1\", \"#A6D281\", \"#6CA140\", \"#487B1E\", \"#FF8148\",\"#FF2817\" ,\"#940B00\"] }"},
  {id: 66, layer_group_id: 8, name: "Pastoral", slug: "pastoral-ful", layer_type: "layer", zindex: nil, active: false, order: nil, color: "", info: "{\"description\":\"Data from the GeoNetwork site of the Food and Agriculture Organisation of the UN; Also derived Famine Early Warning Systems Network (FEWSNet). The Famine Early Warning Systems Network is a leading provider of early warning and analysis on food insecurity. Created by USAID in 1985 to help decision-makers plan for humanitarian crises, FEWS NET provides evidence-based analysis on some 35 countries. Implementing team members include NASA, NOAA, USDA, and USGS, along with Chemonics International Inc. and Kimetrica.\", \"source\":\"FEWSNET. Livelihoods: Insights into how people survive and prosper [Internet]. [updated 21 January, 2015]. Available: https://www.fews.net/sectors/livelihoods [cited 21 October 2015].\", \"link\":\"http://www.fao.org/geonetwork/srv/en/main.home\" }", layer_provider: "cartodb", css: " #grp_africa_livelihoodzones[lz_type=\"Pastoral\"] {\r\n  polygon-opacity: 0;  \r\n  polygon-pattern-file: url('https://s3.amazonaws.com/com.cartodb.users-assets.production/production/simbiotica/assets/20150916141715tile.png');\r\n  line-offset:-.75;\r\n  line-color:#6E3600;\r\n  line-opacity:1;\r\n  polygon-pattern-opacity:1;\r\n }", interactivity: "", opacity: 1.0, query: "SELECT the_geom_webmercator,iso3,lz_type, lz_value FROM grp_africa_livelihoodzones union SELECT the_geom_webmercator,iso3,lz_type, lz_value FROM grp_asia_livelihoodzones", created_at: "2015-10-22 11:18:05", updated_at: "2015-10-22 11:27:42", locate_layer: false, icon_class: "", published: true, legend: "{\"type\": \"custom\", \"data\": [ { \"name\": \"Pastoral\", \"value\": \"#6E3600\" }]}"},
  {id: 6, layer_group_id: 8, name: "Livelihoods zones", slug: "livelihood", layer_type: "layer", zindex: nil, active: false, order: nil, color: "", info: "{\"description\":\"Data from the GeoNetwork site of the Food and Agriculture Organisation of the UN; Also derived Famine Early Warning Systems Network (FEWSNet). The Famine Early Warning Systems Network is a leading provider of early warning and analysis on food insecurity. Created by USAID in 1985 to help decision-makers plan for humanitarian crises, FEWS NET provides evidence-based analysis on some 35 countries. Implementing team members include NASA, NOAA, USDA, and USGS, along with Chemonics International Inc. and Kimetrica.\", \"source\":\"FEWSNET. Livelihoods: Insights into how people survive and prosper [Internet]. [updated 21 January, 2015]. Available: https://www.fews.net/sectors/livelihoods [cited 21 October 2015].\", \"link\":\"http://www.fao.org/geonetwork/srv/en/main.home\" }", layer_provider: "cartodb", css: "#grp_africa_livelihoodzones {\r\n   polygon-opacity: 1;\r\n   line-width: 0;\r\n }\r\n #grp_africa_livelihoodzones[lz_type=\"Agro-Forestry\"] {\r\n   polygon-fill: #547D33;\r\n }\r\n #grp_africa_livelihoodzones[lz_type=\"Agro-Pastoral\"] {\r\n   polygon-fill: #81AF4B;\r\n }\r\n #grp_africa_livelihoodzones[lz_type=\"Arid\"] {\r\n   polygon-fill: #F6EED5;\r\n }\r\n #grp_africa_livelihoodzones[lz_type=\"Crops - Floodzone\"] {\r\n   polygon-fill: #7ADCC2;\r\n }\r\n #grp_africa_livelihoodzones[lz_type=\"Crops - Irrigated\"] {\r\n   polygon-fill: #67A1BD;\r\n }\r\n #grp_africa_livelihoodzones[lz_type=\"Crops - Rainfed\"] {\r\n   polygon-fill: #C6741D;\r\n }\r\n #grp_africa_livelihoodzones[lz_type=\"Fishery\"] {\r\n   polygon-fill: #255CB4;\r\n }\r\n #grp_africa_livelihoodzones[lz_type=\"National Park\"] {\r\n   polygon-fill: #B0E5A0;\r\n }\r\n #grp_africa_livelihoodzones[lz_type=\"Pastoral\"] {\r\n   polygon-fill: #CDD13D;\r\n }\r\n #grp_africa_livelihoodzones[lz_type=\"Urban\"] {\r\n   polygon-fill: 