  {id: 2, layer_group_id: 17, name: "Top exports by total production", slug: "top_exports_by_total_production", layer_type: "layer", zindex: nil, active: false, order: nil, color: "", info: "{\"description\":\"Data is from the Food and Agriculture Organization of the United Nations - Statistics Division; Trade indices for crops and livestock indicate the values for export in 1000$.\", \"source\":\"\", \"link\":\"http://faostat3.fao.org/download/Q/QC/E\" }", layer_provider: "cartodb", css: "#trade_crops_livestock_e_all_data_grp {\r\n   polygon-opacity: 0.7;\r\n   line-color: #FFF;\r\n   line-width: 0.5;\r\n   line-opacity: 1;\r\n}\r\n\r\n#trade_crops_livestock_e_all_data_grp[item=\"Animal Vegetable Oil + (Total)\"] {\r\n   polygon-fill: #A6CEE3;\r\n}\r\n#trade_crops_livestock_e_all_data_grp[item=\"Cashew nuts, with shell\"] {\r\n   polygon-fill: #1F78B4;\r\n}\r\n#trade_crops_livestock_e_all_data_grp[item=\"Cassava Equivalent + (Total)\"] {\r\n   polygon-fill: #B2DF8A;\r\n}\r\n#trade_crops_livestock_e_all_data_grp[item=\"Cereals + (Total)\"] {\r\n   polygon-fill: #33A02C;\r\n}\r\n#trade_crops_livestock_e_all_data_grp[item=\"Cocoa, beans\"] {\r\n   polygon-fill: #FB9A99;\r\n}\r\n#trade_crops_livestock_e_all_data_grp[item=\"Meal, meat\"] {\r\n   polygon-fill: #E31A1C;\r\n}\r\n#trade_crops_livestock_e_all_data_grp[item=\"Oilseeds + (Total)\"] {\r\n   polygon-fill: #FDBF6F;\r\n}\r\n#trade_crops_livestock_e_all_data_grp[item=\"Onions, dry\"] {\r\n   polygon-fill: #FF7F00;\r\n}\r\n#trade_crops_livestock_e_all_data_grp[item=\"Sugar,Total (Raw Equiv.) + (Total)\"] {\r\n   polygon-fill: #CAB2D6;\r\n}\r\n#trade_crops_livestock_e_all_data_grp[item=\"Textile Fibres + (Total)\"] {\r\n   polygon-fill: #6A3D9A;\r\n}\r\n#trade_crops_livestock_e_all_data_grp {\r\n   polygon-fill: #DDDDDD;\r\n}", interactivity: "{\"Item\":\"item\", \"Year\":\"y2013\", \"Unit\":\"unit\"", opacity: 1.0, query: "with t1 as (SELECT iso3, item, unit, value, row_number() over ( partition by iso3 order by val