  {layer_group_id: nil, name: "State (degradation, 2013-2015 compared to 2001-2012, MODIS)", slug: "ldmp_prod_state_emerging", layer_type: "layer", zindex: nil, active: false, order: nil, color: nil, info: "{\"description\":\"Emerging state is a comparison of how current productivity (2015) in an area compares to past productivity in that area (2001-2014) using MODIS 250m (MOD13Q1, annual) dataset.\", \r\n\"source\":\"Sims, Neil et. al. 2017. Good Practice Guidance. SDG Indicator 15.3.1. Proportion of land that is degraded over total land area. Version 1.0 Available from: http://www2.unccd.int/sites/default/files/relevant-links/2017-10/Good%20Practice%20Guidance_ SDG%20Indicator%2015.3.1_Version%201.0.pdf \",\r\n\"link\":\"http://vitalsigns.org/gef-ldmp/project-description-and-timeline\"}", layer_provider: "raster", css: "#aus_eme_degr_reclass2  {\r\n raster-opacity:1; \r\n raster-scaling:near; \r\n raster-colorizer-default-mode:discrete; \r\n raster-colorizer-default-color: transparent;\r\n raster-colorizer-stops: \r\nstop(0, #f6f6ea)\r\nstop(1, #008c79)\r\nstop(2, #3a4dd6) \r\nstop(3, #c069df)\r\nstop(4, #993304)\r\nstop(9999, transparent)}", interactivity: "", opacity: 1.0, query: "select * from aus_eme_degr_reclass2", locate_layer: true, icon_class: nil, published: true, legend: "{\"type\": \"custom\", \"data\": [\r\n{\"name\":\"No Data\", \"value\": \"#000000\"},\r\n{\"name\": \"Significant decrease\", \"value\": \"#993304 \"},\r\n{\"name\": \"Water\", \"value\": \"#3a4dd6 \"},\r\n{\"name\": \"No significant change\", \"value\": \"#f6f6ea \"}, \r\n{\"name\": \"Urban land cover\", \"value\": \"#c069df \"},\r\n{\"name\": \"Significant increase\", \"value\": \"#008c79\"} \r\n] }", zoom_max: 100, zoom_min: 0, dashboard_order: nil, download: false, dataset_shortname: nil, dataset_source_url: nil, title: "Productivity state (emerging)", start_date: nil, end_date: nil, spatial_resolution: "", spatial_resolution_units: "", temporal_resolution: "", temporal_resolution_u