  {layer_group_id: nil, name: "Household food security (dry year)", slug: "hhfoodsd", layer_type: "layer", zindex: nil, active: false, order: nil, color: "", info: "{\"description\":\"Based on analysis of Vital Signs and Living Standards Measurement Survey (LSMS) datasets.\", \"source\":\"Vital Signs, Living Standards Measurement Survey.\", \"link\":\"http://vitalsigns.org/get-data\" }", layer_provider: "cartodb", css: "#tza_maize_predictions{ marker-fill-opacity: 0.8; marker-line-color: #FFF; marker-line-width: 0; marker-line-opacity: 1; marker-width: 5; marker-fill: #FFF; marker-allow-overlap: true; } #tza_maize_predictions [food_secure=false] { marker-fill: #EFD22A;  } #tza_maize_predictions [ food_secure =true] { marker-fill: #2EBBAE; }", interactivity: "", opacity: 1.0, query: "SELECT st_transform(ST_SetSRID(ST_MakePoint(x::numeric,y::numeric),4326),3857) as the_geom_webmercator, ST_SetSRID(ST_MakePoint(x::numeric,y::numeric),4326) as the_geom, yield::numeric, scenario, food_secure::boolean FROM tza_maize_predictions where scenario='SPI = -1, extension = observed'", locate_layer: false, icon_class: "", published: true, legend: "{\"min\": \"True\", \"max\": \"False\", \"type\": \"choropleth\", \"bucket\": [ \"#2EBBAE\", \"#EFD22A\" ] }", zoom_max: 100, zoom_min: 0, dashboard_order: 1, download: false, dataset_shortname: "Vital Signs, Living Standards Measurement Survey", dataset_source_url: "http://vitalsigns.org/get-data", title: "Household food security (dry year)", start_date: nil, end_date: nil, spatial_resolution: nil, spatial_resolution_units: nil, temporal_resolution: nil, temporal_resolution_units: nil, data_units: nil, update_frequency: nil, version: nil, processing: nil, description: "Based on analysis of Vital Signs and Living Standards Measurement Survey (LSMS) datasets."},
  {layer_group_id: nil, name: "Tropical cyclones average sum of windspeed 1970-2009", slug: "cyclones_nat_dis_MDG", layer_type: "layer", zindex: nil, active: false, order: nil, color: nil, info: "{\"description\":\"This dataset includes an estimated annual sum of tropical cyclones windspeed (km/hr) 1975-2007. [Accessed 18 November 2016 ]\", \"source\":\"<p> IBTrACS v02r01 (1969 - 2008, http://www.ncdc.noaa.gov/oa/ibtracs/), year 2009 completed by online data from JMA, JTWC, UNISYS, Meteo France and data sent by Alan Sharp from the Australian Bureau of Meteorology<p><p>A GIS modeling based on an initial equation from Greg Holland, which was further modified to take into consideration the movement of the cyclones through time. Unit is km/h x h. This product was designed by UNEP/GRID-Europe for the Global Assessment Report on Risk Reduction (GAR). It was modeled using global data. Credit: Compilation and GIS processing UNEP/GRID-Europe. Available from http://preview.grid.unep.ch..</p>\",\"link\":\"http://preview.grid.unep.ch/index.php?preview=data&events=cyclones&evcat=3&lang=eng\" }", layer_provider: "raster", css: "#cy_sumwind_mdg {\r\n\traster-opacity:1;\r\n\traster-colorizer-default-mode: linear; \r\n\traster-colorizer-default-color: transparent; \r\n\traster-colorizer-epsilon: 0.01;\r\n\traster-colorizer-stops:  \r\n    \tstop(0,transparent)\r\n    \tstop(5,#F1EEF6)\r\n       \tstop(10,#d4b9da)\r\n    \tstop(25,#c994c7)\r\n    \tstop(35,#DF65B0)\r\n    \tstop(75,#DD1C77)\r\n    \tstop(10000,#980043)\r\n}", interactivity: "", opacity: 1.0, query: "select * from cy_sumwind_mdg", locate_layer: true, icon_class: nil, published: true, legend: "{\"type\": \"choropleth\", \"bucket\": [{\"color\":\"#F1EEF6\", \"min-value\":\"0\", \"max-value\":\"50\"},\r\n{\"color\":\"#d4b9da\", \"max-value\":\"100\"},\r\n{\"color\":\"#c994c7\", \"max-value\":\"200\"},\r\n{\"color\":\"#DF65B0\", \"max-value\":\"300\"},\r\n{\"color\":\"#DD1C77\", \"max-value\":\"400\"},\r\n{\"color\":\"#980043\" , \"max-value\":\">650\"}] }", zoom_max: 100, zoom_min: 0, dashboard_order: nil, download: false, dataset_shortname: " IBTrACS v02r01 (1969 - 2008, http://www.ncdc.noaa.gov/oa/ibtracs/), year 2009 completed by online data from JMA, JTWC, UNISYS, Meteo France and data sent by Alan Sharp from the Australian Bureau of Meteorology<p><p>A GIS modeling based on an initial equation from Greg Holland, which was further modified to take into consideration the movement of the cyclones through time. Unit is km/h x h. This product was designed by UNEP/GRID-Europe for the Global Assessment Report on Risk Reduction (GAR). It was modeled using global data. Credit: Compilation and GIS processing UNEP/GRID-Europe. Available from http://preview.grid.unep.ch.. ", dataset_source_url: "http://preview.grid.unep.ch/index.php?preview=data&events=cyclones&evcat=3&lang=eng", title: "Tropical cyclones average sum of windspeed 1970-2009", start_date: nil, end_date: nil, spatial_resolution: "", spatial_resolution_units: "", temporal_resolution: "", temporal_resolution_units: "", data_units: "", update_frequency: "", version: "", processing: "", description: "This dataset includes an estimated annual sum of tropical cyclones windspeed (km/hr) 1975-2007. [Accessed 18 November 2016 ]"},
