  {layer_group_id: nil, name: "Livelihood Zones", slug: "agri_liv_gha", layer_type: "layer", zindex: nil, active: false, order: nil, color: "", info: "{\"description\":\"Livelihood zones based on dominant crops\",\"source\":\"<p>FAO</p>\",\"link\":\"http://www.fao.org/geonetwork/srv/en/\"}", layer_provider: "cartodb", css: "/** category visualization */\r\n\r\n#gha_livelihoods {\r\n   polygon-opacity: 0.7;\r\n   line-color: #FFF;\r\n   line-width: 0.5;\r\n   line-opacity: 1;\r\n}\r\n\r\n#gha_livelihoods[descr=\"10:Rice (commercial) and livestock\"] {\r\n   polygon-fill: #A6CEE3;\r\n}\r\n#gha_livelihoods[descr=\"11:Timber, tree crops (cocoa/oil palm/rubber), mining\"] {\r\n   polygon-fill: #1F78B4;\r\n}\r\n#gha_livelihoods[descr=\"12: Coastal zone: fishing, salt, vegetables\"] {\r\n   polygon-fill: #B2DF8A;\r\n}\r\n#gha_livelihoods[descr=\"1:Cereals (sorghum/millet), legumes, yam, livestock (cattle)\"] {\r\n   polygon-fill: #33A02C;\r\n}\r\n#gha_livelihoods[descr=\"2:Cereals (sorghum/millet), legumes, livestock (small ruminants and guinea fowl)\"] {\r\n   polygon-fill: #FB9A99;\r\n}\r\n#gha_livelihoods[descr=\"3:Maize, rice, tree crops (mango), livestock\"] {\r\n   polygon-fill: #E31A1C;\r\n}\r\n#gha_livelihoods[descr=\"4:Eastern corridor and Upper Volta: yam, cassava, livestock\"] {\r\n   polygon-fill: #FDBF6F;\r\n}\r\n#gha_livelihoods[descr=\"5:Volta lake: fishing, maize, yam\"] {\r\n   polygon-fill: #FF7F00;\r\n}\r\n#gha_livelihoods[descr=\"6:Tubers (yam/cassava), maize, cashew, livestock\"] {\r\n   polygon-fill: #CAB2D6;\r\n}\r\n#gha_livelihoods[descr=\"9:Tree crop (cocoa/oil palm/citrus), poultry (commercial)\"] {\r\n   polygon-fill: #6A3D9A;\r\n}\r\n#gha_livelihoods {\r\n   polygon-fill: #DDDDDD;\r\n}", interactivity: "", opacity: 1.0, query: "SELECT * FROM gha_livelihoods", locate_layer: false, icon_class: "", published: true, legend: "", zoom_max: 22, zoom_min: 0, dashboard_order: nil, download: false, dataset_shortname: "FAO ", dataset_source_url: "http://ww