  {layer_group_id: nil, name: "Sesame Simsim", slug: "sesame_simsim", layer_type: "layer", zindex: nil, active: false, order: nil, color: nil, info: "{\"description\":\"Percent Of Households Growing Sesame Simsim\",\r\n                 \"source\":\"<p>Vital Signs</p>\",\r\n                 \"link\":\"http://www.vitalsigns.org\"}", layer_provider: "cartodb", css: "#Crops_Landscape[zoom >= 9]{polygon-opacity: 0.8; line-color: #666666; line-width: 0.5; line-opacity: 1;}\r\n#Crops_Landscape [sesame_simsim = 0.6667][zoom >= 9] {polygon-fill: #850200;}\r\n#Crops_Landscape [sesame_simsim = 0.1667][zoom >= 9] {polygon-fill: #AC2E15;}\r\n#Crops_Landscape [sesame_simsim = 0.0333][zoom >= 9] {polygon-fill: #D2775D;}\r\n#Crops_Landscape [sesame_simsim = 0][zoom >= 9] {polygon-fill: #F8DFD8;}\r\n\r\n\r\n#Crops_Landscape[zoom < 9]{marker-opacity: 0.8; marker-line-color: #666666; marker-line-width: 0.5; marker-line-opacity: 1;}\r\n#Crops_Landscape [sesame_simsim = 0.6667][zoom < 9] {marker-fill: #850200;}\r\n#Crops_Landscape [sesame_simsim = 0.1667][zoom < 9] {marker-fill: #AC2E15;}\r\n#Crops_Landscape [sesame_simsim = 0.0333][zoom < 9] {marker-fill: #D2775D;}\r\n#Crops_Landscape [sesame_simsim = 0][zoom < 9] {marker-fill: #F8DFD8;}\r\n", interactivity: nil, opacity: 1.0, query: "SELECT l.the_geom_webmercator, round(c.sesame_simsim::numeric, 4) AS \"sesame_simsim\" \r\n                  FROM crops_landscape c JOIN vs_landscape_march_2017 l \r\n                  ON c.country = l.country AND c.landscape=l.landscape_no", locate_layer: true, icon_class: nil, published: true, legend: "{\"type\":\"custom\",\"data\":[{\"value\": \"#F8DFD8\", \"name\":\"0%\"},\r\n{\"value\": \"#D2775D\", \"name\": \"3.33%\"},\r\n{\"value\": \"#AC2E15\", \"name\": \"16.7%\"},\r\n{\"value\": \"#850200\", \"name\": \"66.7%\"}]}", zoom_max: 100, zoom_min: 0, dashboard_order: nil, download: false, dataset_shortname: "Vital Signs ", dataset_source_url: "http://www.vitalsigns.org", title: "Sesame Simsim", start_