  {layer_group_id: nil, name: "Maize yield (dry year)", slug: "res-crop-yield", layer_type: "layer", zindex: nil, active: false, order: nil, color: "", info: "{\"description\":\"Based on analysis of Vital Signs and Living Standards Measurement Survey (LSMS) datasets.\", \"source\":\"Vital Signs, Living Standards Measurement Survey.\", \"link\":\"http://vitalsigns.org/get-data\" }", layer_provider: "cartodb", css: "#tza_maize_predictions{\r\n  marker-fill-opacity: 0.8;\r\n  marker-line-color: #FFF;\r\n  marker-line-width: 0;\r\n  marker-line-opacity: 1;\r\n  marker-width: 5;\r\n  marker-fill: #1a9850;\r\n  marker-allow-overlap: true;\r\n}\r\n#tza_maize_predictions [ yield <= 2631.88227626011] {\r\n   marker-fill: #342D13;\r\n}\r\n#tza_maize_predictions [ yield <= 1802.33974713479] {\r\n   marker-fill: #554C35;\r\n}\r\n#tza_maize_predictions [ yield <= 1348.90148742328] {\r\n   marker-fill: #7D735B;\r\n}\r\n#tza_maize_predictions [ yield <= 1106.14558773633] {\r\n   marker-fill: #ADA48B;\r\n}\r\n#tza_maize_predictions [ yield <= 792.245033997357] {\r\n   marker-fill: #EAE4CA;\r\n}", interactivity: "", opacity: 1.0, query: "SELECT st_transform(ST_SetSRID(ST_MakePoint(x::numeric,y::numeric),4326),3857) as the_geom_webmercator, the_geom, yield::numeric, scenario, food_secure::boolean FROM tza_maize_predictions where scenario='SPI = -1, extension = observed'", locate_layer: false, icon_class: "", published: true, legend: "{\"min\": \"<790\",\"mid\":\"1350\", \"max\": \">1800\",\"units\":\"kg / ha\", \"type\": \"choropleth\", \"bucket\": [ \"#EAE4CA\", \"#ADA48B\", \"#7D735B\", \"#554C35\", \"#342D13\" ] }", zoom_max: 100, zoom_min: 0, dashboard_order: 1, download: false, dataset_shortname: "Vital Signs, Living Standards Measurement Survey", dataset_source_url: "http://vitalsigns.org/get-data", title: "Maize yield (dry year)", start_date: nil, end_date: nil, spatial_resolution: nil, spatial_resolution_units: nil, temporal_resolution: nil, temporal_resolution_units: nil, da