  {layer_group_id: 27, name: "Projected change (2070, RCP4.5) using 30 second resolution", slug: "CHN_rain_future_4.5rcp", layer_type: "layer", zindex: nil, active: false, order: nil, color: "", info: "{\"description\":\"Change in annual mean rainfall for 2050, in mm, compared to the 1960-1990 period. This layer uses downscaled global climate model (GCM) data from CMIP5 (IPPC Fifth Assessment) and IPPC5 climate projections from GCMs for the 8.5 representative concentration pathway (RCP). The GCM output was downscaled and calibrated (bias corrected) using WorldClim 1.4 as baseline current climate.\", \"source\":\"<p>Hijmans, R.J., S.E. Cameron, J.L. Parra, P.G. Jones and A. Jarvis, 2005. Very high resolution interpolated climate surfaces for global land areas. International Journal of Climatology 25: 1965-1978.</p>\", \"link\":\"http://onlinelibrary.wiley.com/doi/10.1002/joc.1276/pdf\" }", layer_provider: "raster", css: "#total_annual_rainfall_multimodel_pctdiff_mean_45_70_chn {\r\nraster-opacity:1;\r\n  raster-scaling:near;\r\n  raster-colorizer-default-mode:linear;\r\n  raster-colorizer-default-color:  transparent;\r\n  raster-colorizer-epsilon:0.41;\r\n  raster-colorizer-stops:\r\n    stop(-35, rgba(111,103,78,1))\r\n    stop(-30, rgba(191,131,46,1))\r\n    stop(-10, rgba(222,193,124,1))\r\n    stop(-5, rgba(245,231,193,1))\r\n    stop(-1, rgba(245,231,193,1))\r\n    stop(0, transparent, exact)\r\n    stop(1, rgba(223,235,247,1))\r\n    stop(5, rgba(168,207,230,1))\r\n    stop(10, rgba(87,161,207,1))\r\n    stop(30, rgba(28,105,176,1))\r\n    stop(50, rgba(7,47,107,1)) \r\n    stop(100, rgba(7,47,107,1));\r\n}", interactivity: "", opacity: 1.0, query: "select * from total_annual_rainfall_multimodel_pctdiff_mean_45_70_chn", locate_layer: true, icon_class: "", published: true, legend: "{ \"min\": \"<-35%\", \"mid\":\"0%\", \"max\": \">50%\",\"units\":\"Percent of 1950-90 mean\", \"type\": \"choropleth\", \"bucket\": [ \"#bf832e\", \"#dec17c\", \"#f5e7c1\", \"#fff\", \"#dfebf7\", \"#a8cfe6\", \"#57a1cf\",\"#072f6b\" ] }", zoom_max: 9, zoom_min: 0, dashboard_order: nil, download: false, dataset_shortname: "Hijmans, R.J., S.E. Cameron, J.L. Parra, P.G. Jones and A. Jarvis, 2005. Very high resolution interpolated climate surfaces for global land areas. International Journal of Climatology 25: 1965-1978. ", dataset_source_url: "http://onlinelibrary.wiley.com/doi/10.1002/joc.1276/pdf", title: "Projected change (2070, RCP4.5) using 30 second resolution", start_date: nil, end_date: nil, spatial_resolution: "", spatial_resolution_units: "", temporal_resolution: "", temporal_resolution_units: "", data_units: "", update_frequency: "", version: "", processing: "", description: "Change in annual mean rainfall for 2050, in mm, compared to the 1960-1990 period. This layer uses downscaled global climate model (GCM) data from CMIP5 (IPPC Fifth Assessment) and IPPC5 climate projections from GCMs for the 8.5 representative concentration pathway (RCP). The GCM output was downscaled and calibrated (bias corrected) using WorldClim 1.4 as baseline current climate."},
