  {layer_group_id: 20, name: "Urban areas (circa 2000)", slug: "", layer_type: "layer", zindex: nil, active: false, order: nil, color: "", info: "{\"description\":\"Map of urban land area produced using satellite imagery from 2000-2001, at 500 meter resolution. Urban areas are areas where the built environment covers at least 50% of the pixel area. The \\\"built environment\\\" is defined as all non-vegetative, human-constructed elements of the landscape.\", \"source\":\"<p>Schneider, A., M. A. Friedl, D. K. McIver, and C. E. Woodcock (2003) Mapping urban areas by fusing multiple sources of coarse resolution remotely sensed data. Photogrammetric Engineering and Remote Sensing, volume 69, pages 1377-1386.</p>\", \"link\":\"http://landcoverchange.com/home/data/global_maps/\" }", layer_provider: "cartodb", css: "/** simple visualization */\r\n\r\n#ne_10m_urban_areas{\r\n  polygon-fill: #993404;\r\n  polygon-opacity: 0.9;\r\n  line-color: #FFF;\r\n  line-width: 0.5;\r\n  line-opacity: 1;\r\n}", interactivity: "", opacity: 1.0, query: "select * from ne_10m_urban_areas", locate_layer: true, icon_class: "", published: true, legend: "{\"type\": \"custom\", \"data\": [ { \"name\": \"Urban area\", \"value\": \"#993404\" }]}", zoom_max: 12, zoom_min: 0, dashboard_order: nil, download: false, dataset_shortname: "Schneider, A., M. A. Friedl, D. K. McIver, and C. E. Woodcock (2003) Mapping urban areas by fusing multiple sources of coarse resolution remotely sensed data. Photogrammetric Engineering and Remote Sensing, volume 69, pages 1377-1386. ", dataset_source_url: "http://landcoverchange.com/home/data/global_maps", title: "Urban areas (circa 2000)", start_date: nil, end_date: nil, spatial_resolution: "", spatial_resolution_units: "", temporal_resolution: "", temporal_resolution_units: "", data_units: "", update_frequency: "", version: "", processing: "", description: "Map of urban land area produced using satellite imagery from 2000-2001, at 500 meter resolution. Urban areas a